import React, { Component } from 'react';
import './Jokeme.scss';
import axios from 'axios';
import meme from "../../assets/images/meme.jpg"


class Jokeme extends Component {
    constructor() {
        super();
        this.categories = [];
        this.interval = this.uploadCategories();
        this.page = 1;
    }

    state = {
        meme: meme,
        defaultMemes: JSON.parse(localStorage.getItem('defaultMemes')) || null,
        categories: JSON.parse(localStorage.getItem('jokeCategories')) || null,
    }

    randomNumber = max => {
        return Math.floor(Math.random() * max);
    }

    setMeme = (meme) => {
        this.setState ({
            meme: meme,
        });

    }

    getInstantMeme = (top, bottom, meme, fontSize = '40', font = 'Impact') => {

        const request = {
            url: 'http://localhost:8080/jokeme',
            method: 'get',
            params: {
                top: top,
                bottom: bottom,
                meme: meme,
                fontSize: fontSize,
                font: font,
            }
        }

        axios(request)
        .then (response => {
            console.log (response.data);
            this.setMeme(response.data.url);
        })
        .catch (error => {

        })
    }

    getCategories (start = 1) {
        const request = {
            url: "https://api.jokes.one/joke/categories/search",
            method: 'get',
            headers: {
                "Accept": "application/json",
                'X-JokesOne-Api-Secret': "enhsxyKbfvDs3NM4GUK7sAeF",
            },
            params: {
                query:"",
                start: start
            }

        }
        axios(request)
        .then(response => {
            const categories = response.data.contents.categories;
            const filteredCategories = categories.map(category => category.name);
            filteredCategories.forEach(category => this.categories.push(category));

            console.log("categories", this.categories);
        })
        .catch(error => {

        });
    }

    handleForm = (e) => {
      e.preventDefault();
      const top = "top";
      const selected = this.randomNumber(this.state.defaultMemes.length);

      const request = {
          url: "https://api.jokes.one/joke/random",
          method: "get",
          headers: {
              "X-JokesOne-Api-Secret": "enhsxyKbfvDs3NM4GUK7sAeF",
              "accept": "application/json",
              "content-type": "application/json"
          }
      }

      axios(request)
      .then(response => {
          const joke = response.data.contents.jokes[0].text;
          this.getInstantMeme(top, joke, this.state.defaultMemes[selected], "16");
      })
      .catch(error => {

      });

    }

    uploadCategories() {
        if (this.page < 3) {
            this.getCategories(this.page++);
        } else {
            clearInterval(this.interval);
        }
    }

    componentDidMount() {
       this.getCategories(1);
    }

  render() {
      if (!this.state.defaultMemes) {
          this.getImages();
          return;
      }

    return (
      <div className="jokeme">
          <p>{this.state.defaultMemes.length}</p>
          <form onSubmit={this.handleForm}>
              <label>
                  <p>Top Text</p>
                  <input type="text" name="topText"></input>
              </label>
              <button>InstantMeme</button>
          </form>
          <img className="jokeme__meme" src={this.state.meme}/>
      </div>
    )
  }
}

export default Jokeme
